import React from 'react'

function TextForm(props){
    return(
        <>
        <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea heading="Enter The text Below" className="form-control"  rows="8"></textarea>
        <button className="btn btn-">Convert TO Uppercase</button>
        </div>
</>
    )
}

export default TextForm
