import React,{useState} from 'react'

function TextForm(props){
    const [text,setText]=useState("Enter Text Here")
    function changeText(){
        let UpperText=text.toUpperCase();
        setText(UpperText)
        console.log("clicked")
    }
    function handleOnChnage(event){
        

        setText("")
        setText(event.target.value)
       
    }
   

    return(
        <>
        <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea onChange={handleOnChnage} value={text} heading="Enter The text Below" className="form-control"  rows="8"></textarea>
        <button onClick={changeText} className="btn btn-primary">Convert TO Uppercase</button>
        </div>
</>
    )
}

export default TextForm
